// Validiere die Export-Daten von Teil 2
const exportData = $input.item.json;

// Prüfe ob CSV-Export vorhanden ist
if (!exportData.csv_export) {
  throw new Error('Kein CSV-Export von Teil 2 erhalten');
}

// Prüfe Export-ID
if (!exportData.export_info?.export_id) {
  throw new Error('Keine Export-ID gefunden');
}

// Prüfe Statistiken
if (!exportData.datev_statistiken) {
  throw new Error('Keine DATEV-Statistiken gefunden');
}

return {
  json: exportData
};

// Bestimme Übertragungsweg basierend auf Konfiguration
const exportData = $input.item.json;

// Standard-Konfiguration (kann aus externer Quelle geladen werden)
const uebertragungsConfig = {
  primary_method: 'email', // email, sftp, cloud
  steuerberater_email: 'steuerberater@kanzlei-beispiel.de',
  encryption_required: true,
  backup_methods: ['cloud'],
  notification_recipients: ['buchhaltung@company.com'],
  file_retention_days: 2555 // 7 Jahre GoBD
};

// Dateiname für Export generieren
const zeitraum = exportData.export_info.zeitraum;
const exportId = exportData.export_info.export_id;
const fileName = `DATEV_Export_${zeitraum.von}_bis_${zeitraum.bis}_${exportId.replace(/[^a-zA-Z0-9]/g, '_')}.csv`;

// E-Mail-Betreff und -Text vorbereiten
const emailSubject = `DATEV-Export ${zeitraum.von} bis ${zeitraum.bis} - ${exportData.datev_statistiken.anzahl_buchungen} Buchungen`;
const emailBody = `Sehr geehrte Damen und Herren,

im Anhang finden Sie den DATEV-Export für den Zeitraum vom ${zeitraum.von} bis ${zeitraum.bis}.

**Export-Details:**
- Export-ID: ${exportId}
- Anzahl Buchungen: ${exportData.datev_statistiken.anzahl_buchungen}
- Davon Belege: ${exportData.datev_statistiken.anzahl_belege}
- Davon Rechnungen: ${exportData.datev_statistiken.anzahl_rechnungen}
- Gesamtsumme: ${exportData.datev_statistiken.gesamtsumme.toFixed(2)} €
- Dateigröße: ${exportData.datev_statistiken.export_groesse_kb} KB
- Exportiert am: ${new Date().toLocaleDateString('de-DE')}

Die Daten sind im DATEV ASCII-Format aufbereitet und können direkt importiert werden.

Bei Fragen stehen wir Ihnen gerne zur Verfügung.

Mit freundlichen Grüßen
Ihr Buchhaltungsteam`;

return {
  json: {
    ...exportData,
    uebertragung: {
      ...uebertragungsConfig,
      fileName,
      emailSubject,
      emailBody,
      timestamp: new Date().toISOString()
    }
  }
};

// Dokumentiere die erfolgreiche Übertragung
const data = $input.item.json;
const emailResult = $node["E-Mail an Steuerberater"].json || {};
const cloudResult = $node["In Cloud archivieren"].json || {};

// Übertragungsprotokoll erstellen
const uebertragungsProtokoll = {
  export_id: data.export_info.export_id,
  zeitraum: data.export_info.zeitraum,
  uebertragung_timestamp: new Date().toISOString(),
  empfaenger: data.uebertragung.steuerberater_email,
  dateiname: data.uebertragung.fileName,
  dateigröße_kb: data.datev_statistiken.export_groesse_kb,
  anzahl_buchungen: data.datev_statistiken.anzahl_buchungen,
  email_erfolgreich: !!emailResult.messageId,
  email_message_id: emailResult.messageId || '',
  cloud_archivierung_erfolgreich: !!cloudResult.fileUrl,
  cloud_url: cloudResult.fileUrl || '',
  status: 'uebertragen',
  gobd_konform: true,
  aufbewahrung_bis: new Date(Date.now() + (data.uebertragung.file_retention_days * 24 * 60 * 60 * 1000)).toISOString()
};

return {
  json: {
    ...data,
    uebertragungsProtokoll,
    workflow_abgeschlossen: true
  }
};

