// Template-Rendering-Engine
function renderTemplate(template, data) {
  let rendered = template;

  // Einfache Template-Variable-Ersetzung
  Object.entries(data).forEach(([key, value]) => {
    const regex = new RegExp(`{{${key}}}`, 'g');
    rendered = rendered.replace(regex, value || '');
  });

  // Conditional Logic (vereinfacht)
  rendered = rendered.replace(/{{#if (\w+)}}([\s\S]*?){{/if}}/g, (match, condition, content) => {
    return data[condition] ? content : '';
  });

  return rendered;
}

// Erstelle personalisierte Nachrichten fÃ¼r alle AuslÃ¶ser
const generatedMessages = triggers.map(trigger => {
  let templateConfig;
  let templateData = {
    customer_name: trigger.customer_name || 'Valued Customer',
    customer_email: trigger.customer_email,
    ...trigger
  };

  // Template-Auswahl basierend auf AuslÃ¶ser-Typ
  switch (trigger.type) {
    case 'overdue_invoice':
      const escalationLevel = trigger.escalation_level || 1;
      templateConfig = templates.overdue_invoice[escalationLevel] || templates.overdue_invoice[1];

      templateData = {
        ...templateData,
        invoice_number: trigger.invoice_number,
        amount: `${(trigger.amount && trigger.amount.toFixed)(2) || '0.00'}`,
        due_date: trigger.due_date,
        days_overdue: trigger.days_overdue,
        new_due_date: new Date(Date.now() + 14 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        final_due_date: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        reminder_fee: '25.00',
        interest: `${((trigger.amount || 0) * 0.08 * (trigger.days_overdue || 0) / 365).toFixed(2)}`,
        total_amount: `${((trigger.amount || 0) + 25 + ((trigger.amount || 0) * 0.08 * (trigger.days_overdue || 0) / 365)).toFixed(2)}`
      };
      break;

    case 'payment_confirmation':
      templateConfig = templates.payment_confirmation.default;
      templateData = {
        ...templateData,
        invoice_number: trigger.invoice_number,
        payment_amount: `${(trigger.payment_amount && trigger.payment_amount.toFixed)(2) || '0.00'}`,
        payment_date: trigger.payment_date,
        has_offers: true,
        offers_text: 'Schauen Sie in unseren Newsletter fÃ¼r exklusive Angebote!'
      };
      break;

    case 'birthday':
      templateConfig = templates.birthday.default;
      const customerSinceYears = (trigger.metadata && trigger.metadata.customer_since) ? 
        new Date().getFullYear() - new Date(trigger.metadata.customer_since).getFullYear() : 1;

      templateData = {
        ...templateData,
        age: (trigger.metadata && trigger.metadata.age) || 'X',
        customer_since_years: customerSinceYears,
        birthday_offer: '10% Rabatt auf Ihre nÃ¤chste Bestellung!'
      };
      break;

    case 'follow_up':
      templateConfig = templates.follow_up.default;
      templateData = {
        ...templateData,
        last_contact_date: (trigger.metadata && trigger.metadata.last_contact_date) || 'vor einiger Zeit',
        has_new_services: true,
        new_services_text: 'Digitale BuchhaltungslÃ¶sungen und erweiterte Reporting-Features'
      };
      break;

    default:
      templateConfig = {
        subject: 'Information von Ihrem Service-Team',
        template: `Sehr geehrte/r {{customer_name}},\n\nwir mÃ¶chten Sie Ã¼ber wichtige Neuigkeiten informieren.\n\nMit freundlichen GrÃ¼ÃŸen\nIhr Team`,
        tone: 'neutral',
        urgency: 'low'
      };
  }

  // Template rendern
  const renderedSubject = renderTemplate(templateConfig.subject, templateData);
  const renderedMessage = renderTemplate(templateConfig.template, templateData);

  return {
    trigger_id: trigger.customer_id + '_' + trigger.type,
    customer_id: trigger.customer_id,
    customer_name: trigger.customer_name,
    customer_email: trigger.customer_email,
    trigger_type: trigger.type,
    priority: trigger.priority,
    communication: {
      subject: renderedSubject,
      message: renderedMessage,
      tone: templateConfig.tone,
      urgency: templateConfig.urgency,
      channel: trigger.customer_email ? 'email' : 'manual',
      template_used: `${trigger.type}_${trigger.escalation_level || 'default'}`,
      created_at: new Date().toISOString(),
      estimated_send_time: new Date(Date.now() + 5 * 60 * 1000).toISOString() // +5 Minuten
    },
    original_trigger: trigger
  };
});

// Nachrichten-Statistiken
const messageStats = {
  total_messages: generatedMessages.length,
  by_type: {
    overdue_invoice: generatedMessages.filter(m => m.trigger_type === 'overdue_invoice').length,
    payment_confirmation: generatedMessages.filter(m => m.trigger_type === 'payment_confirmation').length,
    birthday: generatedMessages.filter(m => m.trigger_type === 'birthday').length,
    follow_up: generatedMessages.filter(m => m.trigger_type === 'follow_up').length
  },
  by_urgency: {
    high: generatedMessages.filter(m => m.communication.urgency === 'high').length,
    medium: generatedMessages.filter(m => m.communication.urgency === 'medium').length,
    low: generatedMessages.filter(m => m.communication.urgency === 'low').length
  },
  ready_for_sending: generatedMessages.filter(m => m.customer_email).length
};

return {
  json: {
    session_id: triggerData.session_id,
    generation_timestamp: new Date().toISOString(),
    generated_messages: generatedMessages,
    message_statistics: messageStats,
    has_messages: generatedMessages.length > 0,
    ready_for_sending: messageStats.ready_for_sending > 0,
    webhook_url: 'http://localhost:5678/webhook/communication-sending'
  }
};

