// Erfolgreiche Nachrichtenerstellung protokollieren
const data = $input.item.json;

const successLog = {
  timestamp: new Date().toISOString(),
  workflow: 'Teil2-Erstellung',
  status: data.has_messages ? 'messages_generated' : 'no_messages',
  session_id: data.session_id,
  message_statistics: data.message_statistics || {},
  template_performance: {
    templates_used: [...new Set((data.generated_messages || []).map(m => (m.communication && m.communication.template_used)))],
    average_generation_time_ms: 50, // Simuliert
    template_engine_version: 'v2.0.0'
  }
};

console.log('Kundenkommunikation Teil 2 erfolgreich abgeschlossen:', JSON.stringify(successLog, null, 2));

return {
  json: {
    ...data,
    execution_log: successLog,
    workflow_completed: true,
    status: 'Teil2-Erfolgreich-Abgeschlossen'
  }
};
