// Analysiere eingehende Nachrichten und bereite Multi-Channel-Versand vor
const data = $input.item.json;
const messages = data.generated_messages || [];

if (messages.length === 0) {
  return {
    json: {
      session_id: data.session_id,
      error: 'Keine Nachrichten zum Versenden erhalten',
      timestamp: new Date().toISOString()
    }
  };
}

// Bereite Versand-Batches vor mit Kanal-Optimierung
const sendingBatches = messages.map(message => {
  // Intelligente Kanalwahl basierend auf Kontext
  let preferredChannels = ['email']; // Standard
  let urgencyLevel = message.communication?.urgency || 'low';

  // Erweitere KanÃ¤le basierend auf Dringlichkeit und Typ
  if (urgencyLevel === 'high' || message.trigger_type === 'overdue_invoice') {
    preferredChannels = ['email', 'sms'];
  }

  if (message.trigger_type === 'birthday' || message.trigger_type === 'follow_up') {
    preferredChannels = ['email', 'whatsapp'];
  }

  // Rechtliche Anforderungen prÃ¼fen
  if (message.trigger_type === 'overdue_invoice' && message.original_trigger?.escalation_level >= 3) {
    preferredChannels.push('postal_mail');
  }

  return {
    ...message,
    sending_config: {
      preferred_channels: preferredChannels,
      primary_channel: preferredChannels[0],
      backup_channels: preferredChannels.slice(1),
      send_immediately: urgencyLevel === 'high',
      scheduled_send_time: urgencyLevel === 'high' ? 
        new Date().toISOString() : 
        new Date(Date.now() + 10 * 60 * 1000).toISOString(), // +10 Min fÃ¼r normale Prio
      tracking_enabled: true,
      requires_delivery_confirmation: message.trigger_type === 'overdue_invoice'
    },
    processing_status: 'ready_for_sending'
  };
});

// Versand-Statistiken
const sendingStats = {
  total_messages: sendingBatches.length,
  channels_used: {
    email: sendingBatches.filter(b => b.sending_config.preferred_channels.includes('email')).length,
    sms: sendingBatches.filter(b => b.sending_config.preferred_channels.includes('sms')).length,
    whatsapp: sendingBatches.filter(b => b.sending_config.preferred_channels.includes('whatsapp')).length,
    postal_mail: sendingBatches.filter(b => b.sending_config.preferred_channels.includes('postal_mail')).length
  },
  urgency_distribution: {
    high: sendingBatches.filter(b => b.communication?.urgency === 'high').length,
    medium: sendingBatches.filter(b => b.communication?.urgency === 'medium').length,
    low: sendingBatches.filter(b => b.communication?.urgency === 'low').length
  },
  immediate_sends: sendingBatches.filter(b => b.sending_config.send_immediately).length
};

return {
  json: {
    session_id: data.session_id,
    sending_preparation_completed: new Date().toISOString(),
    sending_batches: sendingBatches,
    sending_statistics: sendingStats,
    ready_for_delivery: true,
    batch_id: `SEND_${new Date().toISOString().replace(/[:.]/g, '_')}`
  }
};

// Multi-Channel-Versand mit Fallback-Logik
const batchData = $node["ğŸ“¨ Nachrichten aufteilen"].json;
const currentMessage = batchData.sending_batches[$node["ğŸ“¨ Nachrichten aufteilen"].context.currentIndex];
const sendingResults = [];
let primaryDeliverySuccess = false;

// === E-MAIL-VERSAND ===
if (currentMessage.sending_config.preferred_channels.includes('email') && currentMessage.customer_email) {
  try {
    // Simuliere E-Mail-Versand (wÃ¼rde normalerweise SendEmail-Node verwenden)
    const emailResult = {
      channel: 'email',
      status: 'sent',
      recipient: currentMessage.customer_email,
      subject: currentMessage.communication.subject,
      sent_at: new Date().toISOString(),
      delivery_id: `EMAIL_${Date.now()}`,
      tracking_id: `TRK_${Math.random().toString(36).substr(2, 9)}`,
      estimated_delivery: new Date(Date.now() + 30000).toISOString() // +30 Sekunden
    };

    sendingResults.push(emailResult);
    primaryDeliverySuccess = true;
  } catch (error) {
    sendingResults.push({
      channel: 'email',
      status: 'failed',
      error: error.message,
      failed_at: new Date().toISOString()
    });
  }
}

// === SMS-VERSAND (bei Bedarf) ===
if (currentMessage.sending_config.preferred_channels.includes('sms') && 
    (currentMessage.communication.urgency === 'high' || !primaryDeliverySuccess)) {

  // Simuliere SMS-Versand
  const smsText = `${currentMessage.communication.subject}\n\n${currentMessage.communication.message.substring(0, 140)}...`;

  const smsResult = {
    channel: 'sms',
    status: 'sent',
    recipient: '+49123456789', // WÃ¼rde aus Kundendaten kommen
    message: smsText,
    sent_at: new Date().toISOString(),
    delivery_id: `SMS_${Date.now()}`,
    cost_eur: 0.08 // SMS-Kosten
  };

  sendingResults.push(smsResult);
}

// === WHATSAPP-VERSAND (bei geeigneten Kunden) ===
if (currentMessage.sending_config.preferred_channels.includes('whatsapp') &&
    currentMessage.trigger_type !== 'overdue_invoice') {

  const whatsappResult = {
    channel: 'whatsapp',
    status: 'sent',
    recipient: '+49123456789',
    template_used: 'customer_communication_v2',
    sent_at: new Date().toISOString(),
    delivery_id: `WA_${Date.now()}`
  };

  sendingResults.push(whatsappResult);
}

// === POSTAL-MAIL (fÃ¼r rechtliche Anforderungen) ===
if (currentMessage.sending_config.preferred_channels.includes('postal_mail')) {
  const postalResult = {
    channel: 'postal_mail',
    status: 'queued',
    service_provider: 'deutsche_post',
    service_type: currentMessage.sending_config.requires_delivery_confirmation ? 'einschreiben' : 'standard',
    queued_at: new Date().toISOString(),
    estimated_delivery: new Date(Date.now() + 2 * 24 * 60 * 60 * 1000).toISOString(), // +2 Tage
    delivery_id: `POST_${Date.now()}`,
    cost_eur: currentMessage.sending_config.requires_delivery_confirmation ? 4.65 : 0.85
  };

  sendingResults.push(postalResult);
}

// Delivery-Status bewerten
const deliverySuccess = sendingResults.some(r => r.status === 'sent' || r.status === 'queued');
const totalCost = sendingResults.reduce((sum, r) => sum + (r.cost_eur || 0), 0);

return {
  json: {
    ...batchData,
    current_message: {
      ...currentMessage,
      delivery_results: sendingResults,
      delivery_success: deliverySuccess,
      total_delivery_cost: totalCost,
      delivery_completed_at: new Date().toISOString(),
      tracking_urls: sendingResults
        .filter(r => r.tracking_id)
        .map(r => `https://tracking.company.com/${r.tracking_id}`)
    },
    batch_progress: {
      current: $node["ğŸ“¨ Nachrichten aufteilen"].context.currentIndex + 1,
      total: batchData.sending_batches.length,
      percentage: Math.round((($node["ğŸ“¨ Nachrichten aufteilen"].context.currentIndex + 1) / batchData.sending_batches.length) * 100)
    }
  }
};

// Sammle alle Versand-Ergebnisse und erstelle finale Statistiken
const allBatches = $node["ğŸ“¨ Nachrichten aufteilen"].json.sending_batches || [];
const currentIndex = $node["ğŸ“¨ Nachrichten aufteilen"].context.currentIndex;
const processedMessage = $input.item.json.current_message;

// Aktualisiere das Batch im Array
allBatches[currentIndex] = processedMessage;

// PrÃ¼fe ob das der letzte Batch ist
if (currentIndex === allBatches.length - 1) {
  // Erstelle finale Versand-Statistiken
  const finalStats = {
    total_messages: allBatches.length,
    successful_deliveries: allBatches.filter(m => m.delivery_success).length,
    failed_deliveries: allBatches.filter(m => !m.delivery_success).length,
    channels_used: {
      email: allBatches.reduce((sum, m) => sum + (m.delivery_results?.filter(r => r.channel === 'email' && r.status === 'sent').length || 0), 0),
      sms: allBatches.reduce((sum, m) => sum + (m.delivery_results?.filter(r => r.channel === 'sms' && r.status === 'sent').length || 0), 0),
      whatsapp: allBatches.reduce((sum, m) => sum + (m.delivery_results?.filter(r => r.channel === 'whatsapp' && r.status === 'sent').length || 0), 0),
      postal_mail: allBatches.reduce((sum, m) => sum + (m.delivery_results?.filter(r => r.channel === 'postal_mail').length || 0), 0)
    },
    total_cost: allBatches.reduce((sum, m) => sum + (m.total_delivery_cost || 0), 0),
    tracking_available: allBatches.filter(m => m.tracking_urls?.length > 0).length
  };

  // Erfolgsrate berechnen
  const successRate = (finalStats.successful_deliveries / finalStats.total_messages * 100).toFixed(2);

  // Performance-Metriken
  const performanceMetrics = {
    success_rate_percent: parseFloat(successRate),
    average_cost_per_message: finalStats.total_cost / finalStats.total_messages,
    email_success_rate: finalStats.channels_used.email / allBatches.length * 100,
    multi_channel_usage_rate: allBatches.filter(m => m.delivery_results?.length > 1).length / allBatches.length * 100
  };

  return {
    json: {
      session_id: $input.item.json.session_id,
      batch_id: $input.item.json.batch_id,
      delivery_completed: new Date().toISOString(),
      delivered_messages: allBatches,
      delivery_statistics: finalStats,
      performance_metrics: performanceMetrics,
      success_rate: successRate,
      all_deliveries_completed: true,
      requires_follow_up: finalStats.failed_deliveries > 0
    }
  };
} else {
  // Noch nicht fertig, gib aktuellen Zustand zurÃ¼ck
  return {
    json: {
      ...allBatches,
      current_index: currentIndex,
      processing_step: 'delivery_in_progress'
    }
  };
}

